    private void btnGoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGoActionPerformed
        // TODO add your handling code here:
        Vendor vendor=(Vendor) dropDownVendor.getSelectedItem();
        VendorWorkAreaJPanel vendorWorkArea = new VendorWorkAreaJPanel(userProcessContainer, prodList,vendor);
        userProcessContainer.add("VendorWorkAreaJPanel", vendorWorkArea);
        CardLayout layout = (CardLayout) userProcessContainer.getLayout();
        layout.next(userProcessContainer);
    }//GEN-LAST:event_btnGoActionPerformed


