    public ViewProductsJPanel(JPanel userProcessContainer, Vendor vendor, ProductDirectory prodList) {
        initComponents();
        this.prodList = prodList;
        this.userProcessContainer = userProcessContainer;
        this.vendor = vendor;
        populateTable();
    }

    public void populateTable() {
        DefaultTableModel dtm = (DefaultTableModel) tblProducts.getModel();
        dtm.setRowCount(0); // Empties the table
        for (Product p : prodList.getProductList()) {
            if (p.getVendorName().equals(vendor.getName())) {
                Object row[] = new Object[2];
                row[0] = p;
                row[1] = p.getCategory();
                dtm.addRow(row);
            }
        }
    }

    public void populateSearchTable(ArrayList<Product> prod) {
        DefaultTableModel dtm = (DefaultTableModel) tblProducts.getModel();
        dtm.setRowCount(0); // Empties the table

        for (Product p : prod) {
            Object row[] = new Object[2];
            row[0] = p;
            row[1] = p.getCategory();
            dtm.addRow(row);
        }
    }

