    private void btnBackActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBackActionPerformed
        // TODO add your handling code here:
        userProcessContainer.remove(this);
        CardLayout layout = (CardLayout) userProcessContainer.getLayout();
        layout.previous(userProcessContainer);
    }//GEN-LAST:event_btnBackActionPerformed

    private void btnViewProdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnViewProdActionPerformed
        // TODO add your handling code here:
        int selectedRow = tblProducts.getSelectedRow();
        if (selectedRow >= 0) {
            Product p = (Product) tblProducts.getValueAt(selectedRow, 0);
            ViewProductJPanel panel = new ViewProductJPanel(userProcessContainer, p);
            userProcessContainer.add("ViewProductJPanel", panel);
            CardLayout layout = (CardLayout) userProcessContainer.getLayout();
            layout.next(userProcessContainer);
        } else {
            JOptionPane.showMessageDialog(this, "Please select a row!!", "Warning", JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_btnViewProdActionPerformed

    private void btnDeleteProdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteProdActionPerformed
        // TODO add your handling code here:
        int selectedRow = tblProducts.getSelectedRow();
        if (selectedRow >= 0) {
            int result = JOptionPane.showConfirmDialog(this, "Confirm Delete?", "Warning", JOptionPane.YES_NO_OPTION);
            if (result == JOptionPane.YES_OPTION) {
                Product p = (Product) tblProducts.getValueAt(selectedRow, 0);
                prodList.deleteProduct(p);
                JOptionPane.showMessageDialog(this, "Selected Product has been Deleted!");
                populateTable();
            }
        } else {
            JOptionPane.showMessageDialog(this, "Please select a row!!", "Warning", JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_btnDeleteProdActionPerformed

    private void btnSearchProdActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSearchProdActionPerformed
        // TODO add your handling code here:
        if (txtSearchProd.getText().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter the Product Name!!");
        } else {
            ArrayList<Product> result = prodList.searchProducts(txtSearchProd.getText(),vendor.getName());
            if (result.isEmpty()) {
                JOptionPane.showMessageDialog(this, "The Product name does not exists!!", "Information", JOptionPane.INFORMATION_MESSAGE);
            } else {
                populateSearchTable(result);
            }
        }
    }//GEN-LAST:event_btnSearchProdActionPerformed


